mapper.select:
select f.*, p.NAME_ PNAME_, p.DGRM_RESOURCE_NAME_ from f left join p on f.PROC_DEF_ID_ = p.ID_ where 1=1
and ( p.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.ASSIGNEE_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and f.CREATE_TIME_ >= #{pd.lastStart}
and f.CREATE_TIME_ <= #{pd.lastEnd}
and ( f.ASSIGNEE_ = #{pd.USERNAME} or f.ASSIGNEE_ in ${pd.RNUMBERS} )
order by f.CREATE_TIME_ desc

select f.*, p.NAME_ PNAME_, p.DEPLOYMENT_ID_, p.DGRM_RESOURCE_NAME_ from ( select n.* from n where ( n.ASSIGNEE_ = #{pd.USERNAME} or n.ASSIGNEE_ in ${pd.RNUMBERS} ) group by n.EXECUTION_ID_
) f left join p on f.PROC_DEF_ID_ = p.ID_ where 1=1
and ( p.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.NAME_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or f.ASSIGNEE_ LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and f.END_TIME_ >= #{pd.lastStart}
and f.END_TIME_ <= #{pd.lastEnd}
and ( f.ASSIGNEE_ = #{pd.USERNAME} or f.ASSIGNEE_ in ${pd.RNUMBERS} )
and f.END_TIME_ is not NULL order by f.END_TIME_ desc

select ht.*, hv.TEXT_ from ht left join hv on ht.TASK_ID_ = hv.TASK_ID_ where 1=1
and ht.PROC_INST_ID_ = #{PROC_INST_ID_}
order by ht.ID_

	SELECT
    *
	FROM
		act_ru_task
	WHERE
		PROC_INST_ID_ = #{PROC_INST_ID_}
	AND (
		ASSIGNEE_ = #{USERNAME}
		OR ASSIGNEE_ IN ${RNUMBERS}
	)
