mapper.select:
SELECT u.`NAME` as zname, w.NAME, w.TONAME, w.COPYNAME, w.CONTENET, DATE_FORMAT(w.STARTTIME,'%Y-%m-%d %H:%i:%S') as STARTTIME, w.ENDTIME, w.PROC_INST_ID_, w.STATE, w.WORKPLAN_ID, w.CCNAME, r.DEPARTMENT, IF(w.WORKPLAN_ID is null,0,1) as submit, (SELECT ROUND(AVG(t.SCORE),2) FROM oa_workplanmx t WHERE t.WORKPLAN_ID = w.WORKPLAN_ID and t.SCORE is not null ) as SCORE, (SELECT GROUP_CONCAT(s.TITLE) FROM (SELECT tt.TITLE FROM oa_workplanmx tt WHERE tt.WORKPLAN_ID = w.WORKPLAN_ID GROUP BY tt.TITLE) as s) as TITLE FROM sys_user u LEFT JOIN sys_role r ON u.ROLE_ID = r.ROLE_ID LEFT JOIN oa_workplan w ON u.`NAME`=w.`NAME`
and w.STARTTIME >= #{pd.STARTCOMMITTIME}
and w.STARTTIME <= #{pd.ENDTCOMMITIME}
where u.`NAME` !='系统管理员'
and ( u.`NAME` LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or r.DEPARTMENT LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%')
)
ORDER BY r.DEPARTMENT
